/*Table structure for table `reviews` */

DROP TABLE IF EXISTS `reviews`;

CREATE TABLE `reviews` (
  `ReviewID` int NOT NULL AUTO_INCREMENT COMMENT '评价ID',
  `TaskID` int DEFAULT NULL COMMENT '对应的任务ID',
  `ReviewerID` int DEFAULT NULL COMMENT '评价者的用户ID',
  `Rating` int DEFAULT NULL COMMENT '评价等级',
  `COMMENT` varchar(255) DEFAULT NULL COMMENT '评价评论',
  `IsApproved` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否已批准',
  `PublisherID` int DEFAULT NULL COMMENT '委托发布者用户编号',
  `AcceptorID` int DEFAULT NULL COMMENT '委托接收者编号',
  PRIMARY KEY (`ReviewID`),
  CONSTRAINT `reviews_chk_1` CHECK (((`Rating` >= 1) and (`Rating` <= 5)))
) ENGINE=InnoDB AUTO_INCREMENT=15 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='存储用户对委托的评价信息';

