/*Table structure for table `system_announcements` */

DROP TABLE IF EXISTS `system_announcements`;

CREATE TABLE `system_announcements` (
  `announcement_id` int NOT NULL AUTO_INCREMENT COMMENT '公告ID，主键，自增',
  `title` varchar(255) NOT NULL COMMENT '公告标题',
  `content` text NOT NULL COMMENT '公告内容，可使用TEXT类型容纳较长文本',
  `publisher_id` int NOT NULL COMMENT '发布者ID，外键关联用户表或其他相关表',
  `publish_time` datetime NOT NULL COMMENT '发布时间',
  `status` enum('DRAFT','PUBLISHED','WITHDRAWN','WITHDROWN') CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL DEFAULT 'DRAFT' COMMENT '公告状态（如：草稿、已发布、已撤回等）',
  `start_effective_time` datetime DEFAULT NULL COMMENT '公告开始生效时间（如有）',
  `end_effective_time` datetime DEFAULT NULL COMMENT '公告结束生效时间（如有）',
  `is_pinned` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否置顶（0：否，1：是）',
  `created_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',
  `updated_by` int NOT NULL COMMENT '最后更新者编号',
  PRIMARY KEY (`announcement_id`)
