/*Table structure for table `users` */

DROP TABLE IF EXISTS `users`;

CREATE TABLE `users` (
  `UserID` int NOT NULL AUTO_INCREMENT COMMENT '用户ID',
  `Username` varchar(255) NOT NULL COMMENT '用户名',
  `PASSWORD` varchar(255) NOT NULL COMMENT '密码',
  `Email` varchar(255) NOT NULL COMMENT '电子邮件地址',
  `IsActive` tinyint(1) NOT NULL DEFAULT '0' COMMENT '用户是否激活',
  `Role` enum('USER','ADMIN') CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL DEFAULT 'USER' COMMENT '用户角色',
  `CreateTime` datetime NOT NULL COMMENT '注册日期',
  `ActiveTime` datetime DEFAULT NULL COMMENT '激活日期',
  `IsEnabled` tinyint DEFAULT '0' COMMENT '是否启用',
  PRIMARY KEY (`UserID`)
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='存储系统用户信息';

